function landx_admin_scripts() {

	wp_enqueue_script( 'jquery' );
	wp_enqueue_script( 'jquery-ui-dialog' );
	wp_enqueue_style( 'wp-color-picker' );
	wp_enqueue_script('thickbox');
    wp_enqueue_style('thickbox'); 
    wp_enqueue_script('media-upload');
    wp_register_script( 'landx-icon-picker', THEMEURI. '/admin/icon-picker/js/icon-picker.js', array( 'jquery' ), false, true );
	wp_register_script( 'landx-scripts', THEMEURI. '/admin/assets/js/scripts.js', array( 'jquery', 'wp-color-picker' , 'landx-icon-picker'), false, true );
	wp_enqueue_script( 'landx-scripts' );

	// Localize the script with new data
	$icons = landx_icon_sets();
	wp_localize_script( 'landx-scripts', 'iconset', $icons );

}

// Hook into the 'admin_enqueue_scripts' action
add_action( 'admin_enqueue_scripts', 'landx_admin_scripts' );
?>